const { createWorker } = require('tesseract.js');
const { ChatOpenAI } = require('langchain/chat_models/openai');
const { HumanMessage } = require('langchain/schema');

async function extractProductInfo(imageUrl) {
    // OCR with Tesseract
    const worker = await createWorker('eng+ind');
    const { data: { text } } = await worker.recognize(imageUrl);
    await worker.terminate();

    // AI Analysis with LangChain
    const model = new ChatOpenAI({ 
        openAIApiKey: process.env.OPENAI_API_KEY 
    });

    const prompt = `Extract product information from this text: ${text}. 
    Return JSON with: name, description, price (number only).`;

    const response = await model.call([new HumanMessage(prompt)]);
    return JSON.parse(response.content);
}

return await extractProductInfo($input.first().json.imageUrl);
